+package automation;
+
+import org.openqa.selenium.By;
+import org.openqa.selenium.WebDriver;
+import org.openqa.selenium.WebElement;
+import org.openqa.selenium.chrome.ChromeDriver;
+import org.openqa.selenium.chrome.ChromeOptions;
+import org.openqa.selenium.support.ui.ExpectedConditions;
+import org.openqa.selenium.support.ui.WebDriverWait;
+
+import java.time.Duration;
+
+public class IndiaMartSearchTest {
+    
+    public static void main(String[] args) {
+        // Set the path to the ChromeDriver executable
+        // You'll need to download ChromeDriver from https://chromedriver.chromium.org/downloads
+        // and update this path to where you saved it
+        System.setProperty("webdriver.chrome.driver", "path_to_chromedriver.exe");
+        
+        // Configure Chrome options
+        ChromeOptions options = new ChromeOptions();
+        // options.addArguments("--headless"); // Uncomment if you want to run in headless mode
+        
+        // Initialize the WebDriver
+        WebDriver driver = new ChromeDriver(options);
+        
+        try {
+            // Step 1: Go to IndiaMART website
+            System.out.println("Navigating to IndiaMART...");
+            driver.get("https://www.indiamart.com");
+            
+            // Step 2: Find the search box and type "Mobile Phone"
+            System.out.println("Typing 'Mobile Phone' in search box...");
+            WebElement searchBox = driver.findElement(By.id("search-input"));
+            searchBox.sendKeys("Mobile Phone");
+            
+            // Step 3: Click on search button
+            System.out.println("Clicking search button...");
+            WebElement searchButton = driver.findElement(By.id("search-button"));
+            searchButton.click();
+            
+            // Step 4: Verify that results are displayed
+            System.out.println("Verifying search results...");
+            WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(10));
+            WebElement searchResults = wait.until(ExpectedConditions.visibilityOfElementLocated(By.className("search-results")));
+            
+            if (searchResults.isDisplayed()) {
+                System.out.println("TEST PASSED: Search results for 'Mobile Phone' are displayed successfully!");
+            } else {
+                System.out.println("TEST FAILED: Search results are not displayed.");
+            }
+            
+        } catch (Exception e) {
+            System.out.println("TEST FAILED with exception: " + e.getMessage());
+            e.printStackTrace();
+        } finally {
+            // Close the browser
+            driver.quit();
+            System.out.println("Test completed.");
+        }
+    }
+}

