+package automation;
+
+import org.openqa.selenium.*;
+import org.openqa.selenium.chrome.ChromeDriver;
+import org.openqa.selenium.chrome.ChromeOptions;
+import org.openqa.selenium.support.ui.ExpectedConditions;
+import org.openqa.selenium.support.ui.WebDriverWait;
+
+import java.time.Duration;
+import java.util.List;
+
+/**
+ * Enhanced version of the IndiaMART search test with better error handling
+ * and multiple strategies for finding elements.
+ */
+public class IndiaMartSearchTestEnhanced {
+    
+    public static void main(String[] args) {
+        // Set the path to the ChromeDriver executable
+        System.setProperty("webdriver.chrome.driver", "C:/Users/imart/IdeaProjects/LearningJavaATB13X/drivers/chromedriver.exe");
+        
+        // Configure Chrome options
+        ChromeOptions options = new ChromeOptions();
+        options.addArguments("--start-maximized"); // Start with maximized browser
+        
+        // Initialize the WebDriver
+        WebDriver driver = new ChromeDriver(options);
+        WebDriverWait wait = new WebDriverWait(driver, Duration.ofSeconds(10));
+        
+        try {
+            // Step 1: Go to IndiaMART website
+            System.out.println("Navigating to IndiaMART...");
+            driver.get("https://www.indiamart.com");
+            
+            // Take a screenshot of the homepage
+            takeScreenshot(driver, "homepage");
+            
+            // Step 2: Find the search box and type "Mobile Phone"
+            System.out.println("Typing 'Mobile Phone' in search box...");
+            WebElement searchBox = findSearchBox(driver, wait);
+            
+            if (searchBox != null) {
+                searchBox.clear();
+                searchBox.sendKeys("Mobile Phone");
+                System.out.println("Successfully entered 'Mobile Phone' in the search box");
+                
+                // Take a screenshot after typing
+                takeScreenshot(driver, "after_typing");
+                
+                // Step 3: Click on search button
+                System.out.println("Clicking search button...");
+                WebElement searchButton = findSearchButton(driver, searchBox);
+                
+                if (searchButton != null) {
+                    searchButton.click();
+                    System.out.println("Successfully clicked the search button");
+                    
+                    // Step 4: Verify that results are displayed
+                    System.out.println("Verifying search results...");
+                    boolean resultsDisplayed = verifySearchResults(driver, wait);
+                    
+                    // Take a screenshot of the results page
+                    takeScreenshot(driver, "search_results");
+                    
+                    if (resultsDisplayed) {
+                        System.out.println("TEST PASSED: Search results for 'Mobile Phone' are displayed successfully!");
+                    } else {
+                        System.out.println("TEST FAILED: Could not verify search results.");
+                    }
+                } else {
+                    System.out.println("TEST FAILED: Could not find the search button.");
+                }
+            } else {
+                System.out.println("TEST FAILED: Could not find the search box.");
+            }
+            
+        } catch (Exception e) {
+            System.out.println("TEST FAILED with exception: " + e.getMessage());
+            e.printStackTrace();
+            
+            // Take a screenshot on error
+            takeScreenshot(driver, "error");
+        } finally {
+            // Add a delay before closing to see the final state
+            try {
+                Thread.sleep(3000);
+            } catch (InterruptedException e) {
+                e.printStackTrace();
+            }
+            
+            // Close the browser
+            driver.quit();
+            System.out.println("Test completed.");
+        }
+    }
+    
+    /**
+     * Find the search box using multiple strategies
+     */
+    private static WebElement findSearchBox(WebDriver driver, WebDriverWait wait) {
+        WebElement searchBox = null;
+        
+        // List of strategies to find the search box
+        List<By> searchBoxLocators = List.of(
+            // Strategy 1: Find by placeholder text
+            By.xpath("//input[contains(@placeholder, 'product') or contains(@placeholder, 'search')]"),
+            // Strategy 2: Find by common search input attributes
+            By.xpath("//input[@type='text' and (@name='q' or @name='search' or @name='query' or contains(@class, 'search'))]"),
+            // Strategy 3: Find by common search input IDs
+            By.xpath("//input[contains(@id, 'search') or contains(@id, 'query') or contains(@id, 'keyword')]"),
+            // Strategy 4: Find any input with search-related attributes
+            By.xpath("//input[@type='text' and (@title contains 'search' or @aria-label contains 'search')]"),
+            // Strategy 5: Find by common form structure
+            By.xpath("//form//input[@type='text']")
+        );
+        
+        // Try each strategy until we find the search box
+        for (By locator : searchBoxLocators) {
+            try {
+                searchBox = wait.until(ExpectedConditions.elementToBeClickable(locator));
+                System.out.println("Found search box using: " + locator);
+                return searchBox;
+            } catch (TimeoutException e) {
+                System.out.println("Could not find search box using: " + locator);
+            }
+        }
+        
+        // If all strategies fail, try JavaScript as a last resort
+        try {
+            System.out.println("Trying to find search box using JavaScript...");
+            searchBox = (WebElement) ((JavascriptExecutor) driver).executeScript(
+                "return document.querySelector('input[type=\"text\"]');"
+            );
+            if (searchBox != null && searchBox.isDisplayed()) {
+                System.out.println("Found search box using JavaScript");
+                return searchBox;
+            }
+        } catch (Exception e) {
+            System.out.println("JavaScript search failed: " + e.getMessage());
+        }
+        
+        return null;
+    }
+    
+    /**
+     * Find the search button using multiple strategies
+     */
+    private static WebElement findSearchButton(WebDriver driver, WebElement searchBox) {
+        WebElement searchButton = null;
+        
+        // List of strategies to find the search button
+        List<By> searchButtonLocators = List.of(
+            // Strategy 1: Find button by type and text
+            By.xpath("//button[@type='submit' or contains(text(), 'Search')]"),
+            // Strategy 2: Find button by common search button classes
+            By.xpath("//button[contains(@class, 'search') or contains(@class, 'submit')]"),
+            // Strategy 3: Find input with type submit
+            By.xpath("//input[@type='submit']"),
+            // Strategy 4: Find any element with search icon class
+            By.xpath("//*[contains(@class, 'search-icon') or contains(@class, 'fa-search')]"),
+            // Strategy 5: Find button near the search box
+            By.xpath("//input[@type='text']/following::button[1]")
+        );
+        
+        // Try each strategy until we find the search button
+        for (By locator : searchButtonLocators) {
+            try {
+                searchButton = driver.findElement(locator);
+                if (searchButton.isDisplayed() && searchButton.isEnabled()) {
+                    System.out.println("Found search button using: " + locator);
+                    return searchButton;
+                }
+            } catch (NoSuchElementException e) {
+                System.out.println("Could not find search button using: " + locator);
+            }
+        }
+        
+        // If all strategies fail, try to find the closest button to the search box
+        try {
+            System.out.println("Trying to find the closest button to the search box...");
+            // Get the parent form if it exists
+            WebElement form = searchBox.findElement(By.xpath("./ancestor::form"));
+            searchButton = form.findElement(By.xpath(".//button | .//input[@type='submit']"));
+            if (searchButton != null && searchButton.isDisplayed()) {
+                System.out.println("Found search button within the same form");
+                return searchButton;
+            }
+        } catch (Exception e) {
+            System.out.println("Could not find button in the same form: " + e.getMessage());
+        }
+        
+        // If still not found, try JavaScript as a last resort
+        try {
+            System.out.println("Trying to find search button using JavaScript...");
+            searchButton = (WebElement) ((JavascriptExecutor) driver).executeScript(
+                "return document.querySelector('button[type=\"submit\"], input[type=\"submit\"]');"
+            );
+            if (searchButton != null && searchButton.isDisplayed()) {
+                System.out.println("Found search button using JavaScript");
+                return searchButton;
+            }
+        } catch (Exception e) {
+            System.out.println("JavaScript search failed: " + e.getMessage());
+        }
+        
+        return null;
+    }
+    
+    /**
+     * Verify that search results are displayed
+     */
+    private static boolean verifySearchResults(WebDriver driver, WebDriverWait wait) {
+        try {
+            // Wait for the page to load after search
+            wait.until(ExpectedConditions.titleContains("Mobile Phone") || 
+                       ExpectedConditions.urlContains("mobile") ||
+                       ExpectedConditions.urlContains("phone"));
+            
+            System.out.println("Page title after search: " + driver.getTitle());
+            System.out.println("Current URL: " + driver.getCurrentUrl());
+            
+            // Check if the URL or title contains search terms
+            if (driver.getTitle().toLowerCase().contains("mobile") || 
+                driver.getTitle().toLowerCase().contains("phone") ||
+                driver.getCurrentUrl().toLowerCase().contains("mobile") ||
+                driver.getCurrentUrl().toLowerCase().contains("phone")) {
+                System.out.println("URL or title contains search terms");
+                return true;
+            }
+            
+            // Check for common result page elements
+            List<By> resultLocators = List.of(
+                By.xpath("//div[contains(@class, 'product') or contains(@class, 'result')]"),
+                By.xpath("//div[contains(@class, 'listing') or contains(@class, 'search')]"),
+                By.xpath("//div[contains(@id, 'product') or contains(@id, 'result')]"),
+                By.xpath("//h1[contains(text(), 'Mobile') or contains(text(), 'Phone')]")
+            );
+            
+            for (By locator : resultLocators) {
+                try {
+                    List<WebElement> elements = driver.findElements(locator);
+                    if (!elements.isEmpty()) {
+                        System.out.println("Found " + elements.size() + " result elements using: " + locator);
+                        return true;
+                    }
+                } catch (Exception e) {
+                    System.out.println("Error checking result elements: " + e.getMessage());
+                }
+            }
+            
+            // If no specific elements found, check if page content contains search terms
+            String pageSource = driver.getPageSource().toLowerCase();
+            if (pageSource.contains("mobile phone") || 
+                (pageSource.contains("mobile") && pageSource.contains("phone"))) {
+                System.out.println("Page source contains search terms");
+                return true;
+            }
+            
+            return false;
+        } catch (Exception e) {
+            System.out.println("Error verifying search results: " + e.getMessage());
+            return false;
+        }
+    }
+    
+    /**
+     * Take a screenshot for debugging purposes
+     */
+    private static void takeScreenshot(WebDriver driver, String name) {
+        try {
+            File screenshot = ((TakesScreenshot) driver).getScreenshotAs(OutputType.FILE);
+            System.out.println("Screenshot taken: " + name + " (saved as: " + screenshot.getAbsolutePath() + ")");
+        } catch (Exception e) {
+            System.out.println("Failed to take screenshot: " + e.getMessage());
+        }
+    }
+}

